public class Parser {
	public static String Parsing(String jsonData) {
		try {
			JSONParser jsonParse = new JSONParser();
		
			//JSONParse¿¡ jsonµ¥ÀÌÅÍ¸¦ ³Ö¾î ÆÄ½ÌÇÑ ´ÙÀ½ JSONObject·Î º¯È¯ÇÑ´Ù. 
			JSONObject jsonObj = (JSONObject) jsonParse.parse(jsonData); 		

			JSONArray itemsArray = (JSONArray) jsonObj.get("items");
			
			JSONObject personObject = (JSONObject)itemsArray.get(0);
			return (String)personObject.get("link");
		} 
		catch (ParseException e) {
			e.printStackTrace();
			return "";
		}		
	}
}
